class App{constructor(){this.endY,this.startY,this.scrollLeft,this.arrowStep=100,this.spaceStep=.8*window.innerWidth,this.bindEvents()}bindEvents(){document.addEventListener("touchstart",this.handleTouchScrollStart.bind(this),{passive:!0}),document.addEventListener("touchmove",this.handleTouchScrollMove.bind(this),{passive:!1}),document.addEventListener("keydown",this.handleKeyScroll.bind(this)),window.addEventListener("wheel",this.handleWheelScroll.bind(this),{passive:!1})}handleTouchScrollStart(e){this.startY=e.touches[0].pageY}handleTouchScrollMove(e){e.preventDefault(),this.endY=e.touches[0].pageY;e=.5*(this.startY-this.endY);window.scrollBy({left:e,behavior:"auto"})}handleKeyScroll(e){if(" "==e.key||"Space"==e.code||32==e.keyCode)e.preventDefault(),e.shiftKey?window.scrollBy({left:-this.spaceStep,behavior:"smooth"}):window.scrollBy({left:this.spaceStep,behavior:"smooth"});else switch(e.key){case"ArrowUp":case"ArrowLeft":e.preventDefault(),e.metaKey?window.scrollTo({left:0,behavior:"smooth"}):window.scrollBy({left:-this.arrowStep,behavior:"smooth"});break;case"ArrowDown":case"ArrowRight":e.preventDefault(),e.metaKey?window.scrollTo({left:document.body.scrollWidth,behavior:"smooth"}):window.scrollBy({left:this.arrowStep,behavior:"smooth"})}}handleWheelScroll(e){e.preventDefault();e=Math.abs(e.deltaX)>Math.abs(e.deltaY)?e.deltaX:e.deltaY;window.scrollBy({left:e,behavior:"auto"})}}document.addEventListener("DOMContentLoaded",()=>{new App});